ZONE = [{'zone': 1, 'price': 149.90, 'plus': 13.40}, {'zone': 2, 'price': 152.00, 'plus': 15.60}, {'zone': 3, 'price': 158.10, 'plus': 22.30}, {'zone': 4, 'price': 192.60, 'plus': 31.80}, {'zone': 5, 'price': 215.00, 'plus': 36.40}, {'zone': 6, 'price': 400.00, 'plus': 00.00}]# 6 зону сделал потому что находимся в мосвке это дотсавка курьером


def calculate_delivery(weight, region):
    delivery_price = 0
    for item in DUMP:
        if region == unicode(item[0], 'utf-8'):
            zone_namber = item[1]
            zone = ZONE[zone_namber - 1]
            price = zone['price']
            plus = zone['plus']
            delivery_price = weight//500 * plus + price
            break

    return delivery_price

